const http = require('http');

// A very small 1x1 white pixel base64 jpeg for testing
const base64Image = "data:image/jpeg;base64,";

const data = JSON.stringify({
    student_id: "13739",
    student_name: "Test Student",
    detail: "Cleaning the classroom floor properly.",
    image_before: base64Image,
    image_after: base64Image,
    type: "work_submission"
});

const options = {
    hostname: 'localhost',
    port: 8000,
    path: '/api/save-work',
    method: 'POST',
    headers: {
        'Content-Type': 'application/json',
        'Content-Length': data.length
    }
};

const req = http.request(options, (res) => {
    console.log(`statusCode: ${res.statusCode}`);
    let body = '';
    res.on('data', (d) => body += d);
    res.on('end', () => {
        console.log('Response:', body);
    });
});

req.on('error', (error) => {
    console.error(error);
});

req.write(data);
req.end();
